import React from "react";
import { withRouter } from "react-router-dom";
import Avatar from "components/Avatar";
import {
  Row,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Button
} from "reactstrap";
import moment from "moment";

/**
 * Chat Header.
 */
const ChatHeader = (props) => {
  /**
   * Show user's status
   */
  const status = () => {
    if (props.typing) return "يكتب الآن";
    if (props.contact.status === true) return "متصل الآن";
    if (props.contact.status) return moment(props.contact.status).fromNow();
    return "";
  };

  /**
   * Handle call icon (button) click
   */
  const handleCall = () => {
    console.log("[ChatHeader] Call button clicked");
    if (props.onCall) {
      props.onCall(props.contact);
    }
  };

  return (
    <Row className="heading m-0 align-items-center w-100" style={{ padding: '0.5rem' }}>
      {/* LEFT side icons */}
      <Nav className="d-flex align-items-center" navbar style={{ marginLeft: '0.5rem' }}>
        
        {/* Call button (green) */}
        <Button color="success" onClick={handleCall} style={{ marginRight: '0.5rem' }}>
          <i className="fa fa-phone" />
        </Button>

        {/* Three-dots menu */}
        <UncontrolledDropdown>
          <DropdownToggle tag="a" className="nav-link" style={{ cursor: "pointer" }}>
            <i className="fa fa-ellipsis-v" />
          </DropdownToggle>
          <DropdownMenu left="true">
            {/* If you want it left-aligned, you can remove right */}
            <DropdownItem onClick={() => props.history.push("/password")}>
              تغيير كلمة المرور
            </DropdownItem>
            <DropdownItem divider />
            <DropdownItem onClick={props.logout}>
              تسجيل الخروج
            </DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>
      </Nav>

      {/* Rest of header: avatar & name on the right */}
      <div className="ml-auto d-flex align-items-center" onClick={props.toggle} style={{ cursor: "pointer" }}>
        <Avatar src={props.contact?.avatar} />
        <div className="text-right ml-2">
          <div>{props.contact?.name || ""}</div>
          <small>{status()}</small>
        </div>
      </div>
    </Row>
  );
};

export default withRouter(ChatHeader);
