/**
 * web/src/views/Chat.js
 */

import React from 'react';
import { Row, Spinner, Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ContactHeader, Contacts, ChatHeader, Messages, MessageForm, UserProfile, EditProfile } from 'components';
import socketIO from 'socket.io-client';
import Auth from 'Auth';

class Chat extends React.Component {
    state = {
        contacts: [],
        contact: {},
        userProfile: false,
        profile: false,

        // Existing message-related states
        connected: false,
        messages: [],
        typing: false,
        timeout: null,
        user: null,

        // ------ NEW CALL STATES ------
        callState: 'idle',     // 'idle' | 'calling' | 'receiving' | 'inCall'
        incomingCallModal: false,
        callerInfo: null,

        // WebRTC
        localStream: null,
        remoteStream: null,
        peerConnection: null,
    };

    componentDidMount() {
        // Initialize socket.io connection.
        this.initSocketConnection();
    }

    // ============ SOCKET.IO CONNECTION & EVENTS ============
    initSocketConnection = () => {
        let socket = socketIO(process.env.REACT_APP_SOCKET, {
            query: 'token=' + Auth.getToken(),
        });

        // --- Existing events ---
        socket.on('connect', () => this.setState({ connected: true }));
        socket.on('disconnect', () => this.setState({ connected: false }));
        socket.on('data', this.onData);
        socket.on('message', this.onNewMessage);
        socket.on('user_status', this.updateUsersState);
        socket.on('typing', this.onTypingMessage);
        socket.on('error', this.onSocketError);

        // ---------------- NEW: CALL EVENTS ----------------
        // Incoming call notification from the server
        socket.on('incomingCall', (data) => {
            // data => { from: userId, callerName: ... }
            console.log('Incoming call from:', data);
            this.setState({
                callState: 'receiving',
                incomingCallModal: true,
                callerInfo: data,
            });
        });

        // SDP Offer from the remote peer
        socket.on('offer', ({ sdp, caller }) => {
            console.log('Received offer from:', caller);
            if (this.state.peerConnection) {
                const desc = new RTCSessionDescription(sdp);
                this.state.peerConnection.setRemoteDescription(desc)
                    .then(() => {
                        // Now create the answer
                        return this.state.peerConnection.createAnswer();
                    })
                    .then(answer => {
                        this.state.peerConnection.setLocalDescription(answer);
                        // Send answer via signaling server
                        socket.emit('answer', {
                            sdp: answer,
                            target: caller
                        });
                        // We are officially in a call after sending answer
                        this.setState({ callState: 'inCall' });
                    })
                    .catch(err => console.error(err));
            }
        });

        // SDP Answer from the remote peer
        socket.on('answer', ({ sdp, callee }) => {
            console.log('Received answer from:', callee);
            if (this.state.peerConnection) {
                const desc = new RTCSessionDescription(sdp);
                this.state.peerConnection.setRemoteDescription(desc)
                    .catch(err => console.error(err));
                // Now we are in call
                this.setState({ callState: 'inCall' });
            }
        });

        // ICE candidate from the remote peer
        socket.on('iceCandidate', ({ candidate, from }) => {
            console.log('Received ICE candidate from:', from);
            if (this.state.peerConnection && candidate) {
                this.state.peerConnection.addIceCandidate(new RTCIceCandidate(candidate))
                    .catch(e => console.error(e));
            }
        });

        // The remote user hung up
        socket.on('hangUp', () => {
            console.log('Call ended by remote');
            this.endCall();
        });

        this.setState({ socket });
    };

    // ============ CALLING FUNCTIONS ============
    initiateCall = (contact) => {
        if (!contact.id) return;

        this.setState({
            callState: 'calling',
            contact: contact,
        });

        // Notify the callee
        this.state.socket.emit('callUser', {
            recipientId: contact.id,
            callerName: this.state.user.username  // Or whichever name field
        });

        // Access microphone (audio only, set video=true if needed)
        navigator.mediaDevices.getUserMedia({ audio: true, video: false })
            .then(stream => {
                this.setState({ localStream: stream });
                // Create RTCPeerConnection
                const peerConnection = this.createPeerConnection();

                // Add local tracks
                stream.getTracks().forEach(track => {
                    peerConnection.addTrack(track, stream);
                });

                // Create offer
                peerConnection.createOffer()
                    .then(offer => {
                        peerConnection.setLocalDescription(offer);
                        // Send offer
                        this.state.socket.emit('offer', {
                            sdp: offer,
                            target: contact.id
                        });
                    })
                    .catch(err => console.error('Offer creation error', err));

                this.setState({ peerConnection });
            })
            .catch(err => console.error('getUserMedia error', err));
    };

    acceptCall = () => {
        this.setState({
            incomingCallModal: false,
            callState: 'inCall',
        });

        // Access microphone
        navigator.mediaDevices.getUserMedia({ audio: true, video: false })
            .then(stream => {
                this.setState({ localStream: stream });
                // Create RTCPeerConnection
                const peerConnection = this.createPeerConnection();

                // Add local tracks
                stream.getTracks().forEach(track => {
                    peerConnection.addTrack(track, stream);
                });

                this.setState({ peerConnection });
                // We will wait for 'offer' event to finalize the answer
            })
            .catch(err => console.error('Error accessing mic', err));
    };

    rejectCall = () => {
        this.setState({
            incomingCallModal: false,
            callState: 'idle',
            callerInfo: null
        });
        // Optionally notify the caller that call is rejected
        // e.g., this.state.socket.emit('callRejected', { ... });
    };

    createPeerConnection = () => {
        const configuration = {
            iceServers: [
                { urls: 'stun:stun1.l.google.com:19302' },
                // If you have a TURN server, add it here
            ],
        };
        const peerConnection = new RTCPeerConnection(configuration);

        // On ICE candidate
        peerConnection.onicecandidate = (event) => {
            if (event.candidate) {
                const targetId = this.state.contact.id || (this.state.callerInfo && this.state.callerInfo.from);
                this.state.socket.emit('iceCandidate', {
                    candidate: event.candidate,
                    target: targetId
                });
            }
        };

        // On track (remote stream)
        peerConnection.ontrack = (event) => {
            console.log('Remote track received');
            this.setState({ remoteStream: event.streams[0] });
        };

        // Handle connection state changes
        peerConnection.onconnectionstatechange = () => {
            const state = peerConnection.connectionState;
            console.log('Connection state change:', state);
            if (state === 'disconnected' || state === 'failed' || state === 'closed') {
                // End the call
                this.endCall();
            }
        };

        return peerConnection;
    };

    endCall = () => {
        const { socket, contact, callerInfo, peerConnection, localStream } = this.state;
        const targetId = contact.id || (callerInfo && callerInfo.from);

        // Notify remote side if we are the one ending the call
        if (this.state.callState === 'calling' || this.state.callState === 'inCall') {
            socket.emit('hangUp', { target: targetId });
        }

        // Close peer connection
        if (peerConnection) {
            peerConnection.close();
        }

        // Stop local tracks
        if (localStream) {
            localStream.getTracks().forEach(track => track.stop());
        }

        // Reset call-related state
        this.setState({
            callState: 'idle',
            peerConnection: null,
            localStream: null,
            remoteStream: null,
            callerInfo: null,
            incomingCallModal: false,
        });
    };

    // ============ EXISTING MESSAGING LOGIC (truncated for brevity) ============
    onData = (user, contacts, messages, users) => {
        let contact = contacts[0] || {};
        this.setState({messages, contacts, user, contact}, () => {
            this.updateUsersState(users);
        });
    };

    onNewMessage = (message) => {
        if(message.sender === this.state.contact.id){
            this.setState({typing: false});
            this.state.socket.emit('seen', this.state.contact.id);
            message.seen = true;
        }
        let messages = this.state.messages.concat(message);
        this.setState({messages});
    };

    onTypingMessage = (sender) => {
        if(this.state.contact.id !== sender) return;
        this.setState({typing: sender});
        clearTimeout(this.state.timeout);
        const timeout = setTimeout(() => this.setState({typing: false}), 3000);
        this.setState({timeout});
    };

    onSocketError = (err) => {
        if(err === 'auth_error'){
            Auth.logout();
            this.props.history.push('/login');
        }
    };

    updateUsersState = (users) => {
        let contacts = this.state.contacts;
        contacts.forEach((element, index) => {
            if(users[element.id]) contacts[index].status = users[element.id];
        });
        this.setState({contacts});
        let contact = this.state.contact;
        if(users[contact.id]) contact.status = users[contact.id];
        this.setState({contact});
    };

    sendMessage = (message) => {
        if(!this.state.contact.id) return;
        message.receiver = this.state.contact.id;
        let messages = this.state.messages.concat(message);
        this.setState({messages});
        this.state.socket.emit('message', message);
    };

    sendType = () => {
        if(this.state.contact.id) {
            this.state.socket.emit('typing', this.state.contact.id);
        }
    };

    logout = () => {
        this.state.socket.disconnect();
        Auth.logout();
        this.props.history.push('/');
    };

    // ============ RENDERING ============
    render() {
        // Show a spinner if not connected or data not loaded
        if(!this.state.connected || !this.state.contacts || !this.state.messages){
            return <Spinner id="loader" color="success" />;
        }

        return (
            <Row className="h-100">
                {/* Contacts section */}
                <div id="contacts-section" className="col-6 col-md-4">
                    <ContactHeader user={this.state.user} toggle={this.profileToggle} />
                    <Contacts 
                        contacts={this.state.contacts}
                        messages={this.state.messages}
                        onChatNavigate={this.onChatNavigate} 
                    />
                    
                    {/* Example: "Call" button next to each contact in your Contacts component
                     * or you can do it here in the map if you like. 
                     * For demonstration, let's just show how it might look:
                     */}
                    <div style={{marginTop: '1rem'}}>
                      {this.state.contacts.map(c => (
                        <div key={c.id} style={{marginBottom: '0.5rem'}}>
                          {c.username} &nbsp;
                          <Button color="primary" size="sm" onClick={() => this.initiateCall(c)}>
                            Call
                          </Button>
                        </div>
                      ))}
                    </div>

                    <UserProfile 
                        contact={this.state.contact}
                        toggle={this.userProfileToggle}
                        open={this.state.userProfile} 
                    />
                    <EditProfile 
                        user={this.state.user}
                        toggle={this.profileToggle}
                        open={this.state.profile}
                    />
                </div>
                
                {/* Messages section */}
                <div id="messages-section" className="col-6 col-md-8">
                    <ChatHeader 
                        contact={this.state.contact}
                        typing={this.state.typing}
                        toggle={this.userProfileToggle}
                        logout={this.logout} 
                    />
                    {this.renderChat()}
                    <MessageForm sender={this.sendMessage} sendType={this.sendType} />

                    {/* If in call, show a "Hang Up" button */}
                    {(this.state.callState === 'calling' || this.state.callState === 'inCall') && (
                        <Button color="danger" onClick={this.endCall}>
                            Hang Up
                        </Button>
                    )}

                    {/* Audio element for remote stream */}
                    {this.state.remoteStream && (
                        <audio
                            ref={(audioEl) => {
                                if (audioEl && this.state.remoteStream) {
                                    audioEl.srcObject = this.state.remoteStream;
                                }
                            }}
                            autoPlay
                        />
                    )}
                </div>

                {/* INCOMING CALL MODAL */}
                <Modal isOpen={this.state.incomingCallModal}>
                    <ModalHeader>Incoming Call</ModalHeader>
                    <ModalBody>
                        {this.state.callerInfo && (
                            <p>{this.state.callerInfo.callerName} is calling...</p>
                        )}
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={this.acceptCall}>Accept</Button>
                        <Button color="secondary" onClick={this.rejectCall}>Reject</Button>
                    </ModalFooter>
                </Modal>
            </Row>
        );
    }

    /**
     * Render messages component.
     */
    renderChat = () => {
        const { contact, user } = this.state;
        if(!contact) return null;
        let messages = this.state.messages.filter(
            e => e.sender === contact.id || e.receiver === contact.id
        );
        return <Messages user={user} messages={messages} />;
    };

    onChatNavigate = (contact) => {
        // Switch to the selected contact's chat
        this.setState({contact});
        // Mark unseen messages as seen
        this.state.socket.emit('seen', contact.id);
        let messages = this.state.messages;
        messages.forEach((element, index) => {
            if(element.sender === contact.id) messages[index].seen = true;
        });
        this.setState({messages});
    };

    userProfileToggle = () => this.setState({userProfile: !this.state.userProfile});
    profileToggle = () => this.setState({profile: !this.state.profile});
}

export default Chat;
