import React from "react";
import { Link } from "react-router-dom";
import { Card, Form, Input, Button } from "reactstrap";
import { Error } from "components";
import axios from "axios";
import logo from 'assets/logo.png';

/**
 * Change Password Component.
 */
class Password extends React.Component {

   state = {password: '', newPassword: ''};

    /**
     * Change form handler
     * @param e
     */
    onChange = e => this.setState({
       [e.target.name]: e.target.value, error: null
   });

    /**
     * Form submit handler.
     * @param e
     */
    onSubmit = e => {
       e.preventDefault();
       let data = { password: this.state.password, newPassword: this.state.newPassword };
       axios.post('/api/account/password', data)
       .then(res => {
           this.props.history.push('/')
       })
       .catch(err => {
           this.setState({error: err.response.data.message });
       });
   };

    /**
     * Render Component.
     */
    render(){
       return (
           <Card className="auth col-lg-3 col-sm-6">
               <Form onSubmit={this.onSubmit}>
                   <img src={logo} alt="" width="200" />
                   <h5 className="mb-4">Change Password</h5>
                   <Error error={this.state.error} />
                   <Input type="password" value={this.state.password} name="password" onChange={this.onChange} placeholder="Current Password" required />
                   <Input type="password" value={this.state.newPassword} name="newPassword" onChange={this.onChange} placeholder="New Password" required />
                   <Button block className="mb-3">Change</Button>
                   <small><Link to="/">Back</Link></small>
                   <p className="m-3 mb-3 text-muted">&copy; { new Date().getFullYear() }</p>
               </Form>
           </Card>
       );
   }

}

export default Password;
