import React from "react";
import { withRouter } from "react-router-dom";
import Avatar from "components/Avatar";
import {
  Row,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";
import moment from "moment";

/**
 * Chat Header.
 */
const ChatHeader = (props) => {
  /**
   * Show user's status
   */
  const status = () => {
    if (props.typing) return "يكتب الآن";
    if (props.contact.status === true) return "متصل الآن";
    if (props.contact.status) return moment(props.contact.status).fromNow();
    return "";
  };

  /**
   * Call icon clicked
   */
  const handleCall = () => {
    if (props.onCall) {
      props.onCall(props.contact);
    }
  };

  return (
    <Row className="heading m-0">
      {/* Avatar / Contact info toggles user profile */}
      <div onClick={props.toggle} style={{ cursor: "pointer" }}>
        <Avatar src={props.contact?.avatar} />
      </div>

      <div className="text-right">
        <div>{props.contact?.name || ""}</div>
        <small>{status()}</small>
      </div>

      <Nav className="ml-auto" navbar>
        {/* Phone icon for calling */}
        <div
          onClick={handleCall}
          style={{ cursor: "pointer", marginRight: "1rem" }}
          title="اتصال"
        >
          <i className="fa fa-phone" />
        </div>

        {/* Three-dots menu */}
        <UncontrolledDropdown>
          <DropdownToggle
            tag="a"
            className="nav-link"
            style={{ cursor: "pointer" }}
          >
            <i className="fa fa-ellipsis-v" />
          </DropdownToggle>
          <DropdownMenu right>
            <DropdownItem onClick={() => props.history.push("/password")}>
              تغيير كلمة المرور
            </DropdownItem>
            <DropdownItem divider />
            <DropdownItem onClick={props.logout}>
              تسجيل الخروج
            </DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>
      </Nav>
    </Row>
  );
};

export default withRouter(ChatHeader);
