import React from "react";
import { withRouter } from "react-router-dom";
import Avatar from "components/Avatar";
import { 
  Row, 
  DropdownItem, 
  DropdownMenu, 
  DropdownToggle, 
  Nav, 
  UncontrolledDropdown 
} from "reactstrap";
import moment from "moment";

/**
 * Chat Header.
 */
const ChatHeader = (props) => {

  /**
   * Render user status
   * @returns {string}
   */
  const status = () => {
    if (props.typing) return "يكتب الآن";
    if (props.contact.status === true) return "متصل الآن";
    if (props.contact.status) return moment(props.contact.status).fromNow();
    return "";
  };

  /**
   * When the call button is clicked.
   */
  const handleCall = () => {
    // Optional debugging:
    // console.log("Call button clicked, props.onCall is:", props.onCall);
    if (props.onCall) {
      props.onCall(props.contact);
    }
  };

  return (
    <Row className="heading m-0">
      {/* Avatar / Contact info - toggles profile */}
      <div onClick={props.toggle} style={{ cursor: "pointer" }}>
        <Avatar src={props.contact?.avatar} />
      </div>
      <div className="text-right">
        <div>{props.contact?.name || ""}</div>
        <small>{status()}</small>
      </div>

      {/* Action menu */}
      <Nav className="mr-auto" navbar>
        <UncontrolledDropdown>
          <DropdownToggle tag="a" className="nav-link" style={{ cursor: "pointer" }}>
            <i className="fa fa-ellipsis-v" />
          </DropdownToggle>
          <DropdownMenu right>

            {/* NEW: Call with phone icon */}
            <DropdownItem onClick={handleCall}>
              <i className="fa fa-phone mr-2" />
              اتصال
            </DropdownItem>

            <DropdownItem divider />

            <DropdownItem onClick={() => props.history.push("/password")}>
              تغيير كلمة المرور
            </DropdownItem>

            <DropdownItem divider />

            <DropdownItem onClick={props.logout}>
              تسجيل الخروج
            </DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>
      </Nav>
    </Row>
  );
};

export default withRouter(ChatHeader);
