// ChatHeader.js
import React from 'react';
import { Row, Button } from 'reactstrap';

/**
 * ChatHeader component
 *
 * Props:
 * - contact: current contact object (e.g. { id, name, avatar, ... })
 * - typing: user ID or boolean indicating if the contact is typing
 * - toggle: function to toggle user profile
 * - logout: function to logout
 * - onCall: function to initiate a call (passed from Chat.js)
 */
const ChatHeader = (props) => {
  const { contact, typing, toggle, logout, onCall } = props;

  return (
    <Row className="heading align-items-center">
      {/* Toggle profile (sidebar) icon */}
      <div className="nav-link" onClick={toggle} style={{ cursor: 'pointer' }}>
        <i className="fa fa-bars" />
      </div>

      {/* Contact name / info */}
      <div className="mx-3">
        {/* Show contact name.  Fallback to "Unknown" if name not available */}
        <strong>{contact?.name || 'Unknown Contact'}</strong>
        {/* If 'typing' is truthy (or matches contact), display something */}
        {typing && <small className="text-muted ml-2">typing...</small>}
      </div>

      {/* Call button */}
      <Button
        color="primary"
        size="sm"
        onClick={() => onCall(contact)}
        className="mx-2"
      >
        Call
      </Button>

      {/* Logout icon */}
      <div className="ml-auto nav-link" onClick={logout} style={{ cursor: 'pointer' }}>
        <i className="fa fa-sign-out" />
      </div>
    </Row>
  );
};

export default ChatHeader;
